import { ref, onMounted } from "vue";
import axios from "axios";
import store from "@/store";
export default {
  __name: 'Cart',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const Buy = () => {};
    const Delete = item => {
      axios.delete(`/cart/${item.id}`).then(response => {
        console.log(response);
        // 删除成功后刷新数据
        fetchData();
      }).catch(error => {
        console.error("删除失败:", error);
      });
    };
    const items = ref([]);
    const fetchData = () => {
      axios.get('/cart', {
        params: {
          username: store.state.user.username
        }
      }).then(response => {
        items.value = response.data;
      }).catch(error => {
        console.error("导入购物车失败:", error);
      });
    };
    onMounted(fetchData);
    const __returned__ = {
      Buy,
      Delete,
      items,
      fetchData,
      ref,
      onMounted,
      get axios() {
        return axios;
      },
      get store() {
        return store;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
